import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE = 'http://localhost:8000';

function App() {
  const [customerData, setCustomerData] = useState({
    gender: 'Male',
    age: 35,
    tenure: 12,
    monthly_charges: 65.0,
    total_charges: 1500.0,
    internet_service: 'Fiber optic',
    contract: 'Month-to-month',
    payment_method: 'Electronic check',
    paperless_billing: 'Yes',
    tech_support: 'No',
    online_backup: 'No'
  });

  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCustomerData(prev => ({
      ...prev,
      [name]: name === 'age' || name === 'tenure' ? parseInt(value) || 0 : 
             name === 'monthly_charges' || name === 'total_charges' ? parseFloat(value) || 0 : value
    }));
  };

  const predictChurn = async () => {
    setLoading(true);
    try {
      const response = await axios.post(`${API_BASE}/predict`, customerData);
      setPrediction(response.data);
    } catch (error) {
      console.error('Prediction error:', error);
      alert('Error making prediction. Make sure the API is running on port 8000.');
    }
    setLoading(false);
  };

  const getRiskColor = (riskLevel) => {
    switch(riskLevel) {
      case 'HIGH': return 'bg-red-100 text-red-800 border-red-200';
      case 'MEDIUM': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'LOW': return 'bg-green-100 text-green-800 border-green-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      {/* Header */}
      <div className="max-w-6xl mx-auto">
        <div className="bg-white rounded-lg shadow-lg p-6 mb-8">
          <div className="text-center">
            <h1 className="text-4xl font-bold text-gray-900 mb-2">
              🎯 Customer Churn Predictor
            </h1>
            <p className="text-gray-600">
              OutriX AI/ML Internship - Task 3 | Intelligent Customer Retention System
            </p>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">

          {/* Input Form */}
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-2xl font-semibold mb-6 text-gray-800">
              📋 Customer Information
            </h2>

            <div className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Gender
                  </label>
                  <select 
                    name="gender" 
                    value={customerData.gender}
                    onChange={handleInputChange}
                    className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="Male">Male</option>
                    <option value="Female">Female</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Age
                  </label>
                  <input 
                    type="number" 
                    name="age"
                    value={customerData.age}
                    onChange={handleInputChange}
                    className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500"
                    min="18" max="100"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Tenure (months)
                  </label>
                  <input 
                    type="number" 
                    name="tenure"
                    value={customerData.tenure}
                    onChange={handleInputChange}
                    className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500"
                    min="0" max="72"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Monthly Charges ($)
                  </label>
                  <input 
                    type="number" 
                    name="monthly_charges"
                    value={customerData.monthly_charges}
                    onChange={handleInputChange}
                    className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500"
                    min="20" max="120" step="0.01"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Internet Service
                  </label>
                  <select 
                    name="internet_service"
                    value={customerData.internet_service}
                    onChange={handleInputChange}
                    className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="DSL">DSL</option>
                    <option value="Fiber optic">Fiber optic</option>
                    <option value="No">No Internet</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Contract Type
                  </label>
                  <select 
                    name="contract"
                    value={customerData.contract}
                    onChange={handleInputChange}
                    className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="Month-to-month">Month-to-month</option>
                    <option value="One year">One year</option>
                    <option value="Two year">Two year</option>
                  </select>
                </div>
              </div>

              <button 
                onClick={predictChurn}
                disabled={loading}
                className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 px-6 rounded-md transition duration-200 disabled:opacity-50"
              >
                {loading ? '🤖 Analyzing...' : '🤖 Predict Churn Risk'}
              </button>
            </div>
          </div>

          {/* Results */}
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-2xl font-semibold mb-6 text-gray-800">
              📊 Prediction Results
            </h2>

            {prediction ? (
              <div className="space-y-6">
                {/* Risk Level */}
                <div className="text-center">
                  <div className={`inline-flex items-center px-4 py-2 rounded-full text-lg font-semibold border ${getRiskColor(prediction.risk_level)}`}>
                    <span className="mr-2">
                      {prediction.risk_level === 'HIGH' ? '🔴' : 
                       prediction.risk_level === 'MEDIUM' ? '🟡' : '🟢'}
                    </span>
                    {prediction.risk_level} RISK
                  </div>
                </div>

                {/* Probability */}
                <div className="bg-gray-50 rounded-lg p-4">
                  <h3 className="font-semibold text-gray-800 mb-2">Churn Probability</h3>
                  <div className="flex items-center">
                    <div className="flex-1 bg-gray-200 rounded-full h-4">
                      <div 
                        className="bg-gradient-to-r from-green-400 to-red-500 h-4 rounded-full transition-all duration-500"
                        style={{width: `${prediction.churn_probability * 100}%`}}
                      ></div>
                    </div>
                    <span className="ml-3 font-bold text-lg">
                      {(prediction.churn_probability * 100).toFixed(1)}%
                    </span>
                  </div>
                </div>

                {/* Recommendations */}
                <div className="bg-blue-50 rounded-lg p-4">
                  <h3 className="font-semibold text-gray-800 mb-3">
                    💡 Recommendations
                  </h3>
                  <ul className="space-y-2">
                    {prediction.recommendations.map((rec, index) => (
                      <li key={index} className="flex items-start text-sm">
                        <span className="text-blue-500 mr-2">•</span>
                        <span className="text-gray-700">{rec}</span>
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            ) : (
              <div className="text-center py-12 text-gray-500">
                <div className="text-6xl mb-4">🤖</div>
                <p className="text-lg">Enter customer data and predict churn risk</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
