import React, { useState } from 'react';
import axios from 'axios';

const API_BASE = 'http://localhost:8000';

// Professional Blue-Teal Color Palette
const styles = {
  container: {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #0f4c75 0%, #3282b8 50%, #0f4c75 100%)',
    fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, sans-serif"
  },
  navbar: {
    background: 'rgba(255, 255, 255, 0.1)',
    backdropFilter: 'blur(10px)',
    padding: '20px 0',
    borderBottom: '1px solid rgba(255, 255, 255, 0.2)'
  },
  navContent: {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '0 20px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between'
  },
  logo: {
    color: 'white',
    fontSize: '1.5rem',
    fontWeight: '700',
    display: 'flex',
    alignItems: 'center',
    gap: '10px'
  },
  badge: {
    background: 'rgba(255, 255, 255, 0.2)',
    color: 'white',
    padding: '4px 12px',
    borderRadius: '20px',
    fontSize: '0.8rem',
    fontWeight: '500'
  },
  heroSection: {
    textAlign: 'center',
    padding: '60px 20px',
    color: 'white'
  },
  heroTitle: {
    fontSize: '3.5rem',
    fontWeight: '800',
    marginBottom: '20px',
    background: 'linear-gradient(135deg, #ffffff 0%, #e0f7fa 100%)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
    textShadow: '0 4px 8px rgba(0,0,0,0.3)'
  },
  heroSubtitle: {
    fontSize: '1.3rem',
    marginBottom: '10px',
    color: 'rgba(255, 255, 255, 0.9)'
  },
  heroDescription: {
    fontSize: '1rem',
    color: 'rgba(255, 255, 255, 0.7)',
    maxWidth: '600px',
    margin: '0 auto'
  },
  mainContent: {
    maxWidth: '1400px',
    margin: '0 auto',
    padding: '0 20px 40px',
    display: 'grid',
    gridTemplateColumns: '1fr 1fr',
    gap: '40px',
    marginTop: '-40px'
  },
  card: {
    background: 'rgba(255, 255, 255, 0.95)',
    backdropFilter: 'blur(20px)',
    borderRadius: '24px',
    padding: '40px',
    boxShadow: '0 20px 60px rgba(15, 76, 117, 0.2), 0 8px 32px rgba(50, 130, 184, 0.1)',
    border: '1px solid rgba(255, 255, 255, 0.2)',
    position: 'relative',
    overflow: 'hidden'
  },
  cardGlow: {
    position: 'absolute',
    top: '-50%',
    left: '-50%',
    width: '200%',
    height: '200%',
    background: 'radial-gradient(circle, rgba(50, 130, 184, 0.1) 0%, transparent 70%)',
    pointerEvents: 'none'
  },
  cardTitle: {
    fontSize: '1.8rem',
    fontWeight: '700',
    marginBottom: '30px',
    color: '#1a202c',
    display: 'flex',
    alignItems: 'center',
    gap: '12px'
  },
  inputGroup: {
    marginBottom: '24px',
    position: 'relative'
  },
  label: {
    display: 'block',
    fontSize: '0.9rem',
    fontWeight: '600',
    color: '#2d3748',
    marginBottom: '8px',
    textTransform: 'uppercase',
    letterSpacing: '0.5px'
  },
  input: {
    width: '100%',
    padding: '16px 20px',
    border: '2px solid #e2e8f0',
    borderRadius: '12px',
    fontSize: '16px',
    transition: 'all 0.3s ease',
    background: 'rgba(255, 255, 255, 0.8)',
    boxSizing: 'border-box'
  },
  inputFocus: {
    border: '2px solid #3282b8',
    boxShadow: '0 0 0 3px rgba(50, 130, 184, 0.1)',
    outline: 'none'
  },
  inputGrid: {
    display: 'grid',
    gridTemplateColumns: '1fr 1fr',
    gap: '20px',
    marginBottom: '20px'
  },
  button: {
    width: '100%',
    background: 'linear-gradient(135deg, #0f4c75 0%, #3282b8 50%, #0369a1 100%)',
    color: 'white',
    padding: '18px 24px',
    border: 'none',
    borderRadius: '16px',
    fontSize: '18px',
    fontWeight: '600',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    boxShadow: '0 8px 32px rgba(50, 130, 184, 0.3)',
    position: 'relative',
    overflow: 'hidden'
  },
  buttonHover: {
    transform: 'translateY(-2px)',
    boxShadow: '0 12px 40px rgba(50, 130, 184, 0.4)'
  },
  buttonDisabled: {
    background: 'linear-gradient(135deg, #a0aec0 0%, #cbd5e0 100%)',
    cursor: 'not-allowed',
    transform: 'none',
    boxShadow: '0 4px 16px rgba(160, 174, 192, 0.3)'
  },
  resultsEmpty: {
    textAlign: 'center',
    padding: '60px 20px',
    color: '#718096'
  },
  resultsEmptyIcon: {
    fontSize: '4rem',
    marginBottom: '20px',
    display: 'block'
  },
  riskIndicator: {
    textAlign: 'center',
    marginBottom: '32px'
  },
  riskBadge: {
    display: 'inline-flex',
    alignItems: 'center',
    gap: '12px',
    padding: '16px 32px',
    borderRadius: '50px',
    fontSize: '1.2rem',
    fontWeight: '700',
    textTransform: 'uppercase',
    letterSpacing: '1px',
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
    border: '2px solid'
  },
  riskHigh: {
    background: 'linear-gradient(135deg, #fee2e2 0%, #fecaca 100%)',
    color: '#dc2626',
    borderColor: '#f87171'
  },
  riskMedium: {
    background: 'linear-gradient(135deg, #fef3c7 0%, #fde68a 100%)',
    color: '#d97706',
    borderColor: '#facc15'
  },
  riskLow: {
    background: 'linear-gradient(135deg, #d1fae5 0%, #a7f3d0 100%)',
    color: '#059669',
    borderColor: '#34d399'
  },
  probabilitySection: {
    background: 'linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 100%)',
    borderRadius: '20px',
    padding: '32px',
    marginBottom: '32px',
    textAlign: 'center'
  },
  probabilityTitle: {
    fontSize: '1.1rem',
    fontWeight: '600',
    color: '#0369a1',
    marginBottom: '20px',
    textTransform: 'uppercase',
    letterSpacing: '0.5px'
  },
  probabilityValue: {
    fontSize: '3rem',
    fontWeight: '800',
    background: 'linear-gradient(135deg, #0f4c75 0%, #3282b8 100%)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
    marginBottom: '24px'
  },
  progressContainer: {
    position: 'relative',
    width: '200px',
    height: '200px',
    margin: '0 auto'
  },
  progressInner: {
    width: '160px',
    height: '160px',
    backgroundColor: 'white',
    borderRadius: '50%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)'
  },
  recommendationsBox: {
    background: 'linear-gradient(135deg, #ecfdf5 0%, #d1fae5 100%)',
    borderRadius: '20px',
    padding: '32px',
    border: '2px solid #6ee7b7'
  },
  recommendationsTitle: {
    fontSize: '1.3rem',
    fontWeight: '700',
    color: '#047857',
    marginBottom: '20px',
    display: 'flex',
    alignItems: 'center',
    gap: '10px'
  },
  recommendationItem: {
    background: 'rgba(255, 255, 255, 0.8)',
    padding: '16px 20px',
    borderRadius: '12px',
    marginBottom: '12px',
    fontSize: '0.95rem',
    color: '#1f2937',
    display: 'flex',
    alignItems: 'flex-start',
    gap: '12px',
    boxShadow: '0 4px 16px rgba(0, 0, 0, 0.05)'
  },
  footer: {
    textAlign: 'center',
    padding: '40px 20px',
    color: 'rgba(255, 255, 255, 0.8)'
  }
};

function App() {
  const [customerData, setCustomerData] = useState({
    gender: 'Male',
    age: 35,
    tenure: 12,
    monthly_charges: 65.0,
    total_charges: 1500.0,
    internet_service: 'Fiber optic',
    contract: 'Month-to-month',
    payment_method: 'Electronic check',
    paperless_billing: 'Yes',
    tech_support: 'No',
    online_backup: 'No'
  });

  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);
  const [buttonHovered, setButtonHovered] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCustomerData(prev => ({
      ...prev,
      [name]: name === 'age' || name === 'tenure' ? parseInt(value) || 0 : 
             name === 'monthly_charges' || name === 'total_charges' ? parseFloat(value) || 0 : value
    }));
  };

  const predictChurn = async () => {
    setLoading(true);
    try {
      const response = await axios.post(`${API_BASE}/predict`, customerData);
      setPrediction(response.data);
    } catch (error) {
      console.error('Prediction error:', error);
      alert('Error making prediction. Make sure the API is running on port 8000.');
    }
    setLoading(false);
  };

  const getRiskStyle = (riskLevel) => {
    switch(riskLevel) {
      case 'HIGH': return { ...styles.riskBadge, ...styles.riskHigh };
      case 'MEDIUM': return { ...styles.riskBadge, ...styles.riskMedium };
      case 'LOW': return { ...styles.riskBadge, ...styles.riskLow };
      default: return { ...styles.riskBadge, ...styles.riskLow };
    }
  };

  const CircularProgress = ({ percentage }) => {
    const circumference = 2 * Math.PI * 80;
    const strokeDasharray = `${(percentage / 100) * circumference} ${circumference}`;

    return (
      <div style={styles.progressContainer}>
        <svg width="200" height="200" style={{position: 'absolute', transform: 'rotate(-90deg)'}}>
          <circle
            cx="100"
            cy="100"
            r="80"
            stroke="#e2e8f0"
            strokeWidth="12"
            fill="transparent"
          />
          <circle
            cx="100"
            cy="100"
            r="80"
            stroke="url(#blueGradient)"
            strokeWidth="12"
            fill="transparent"
            strokeDasharray={strokeDasharray}
            strokeLinecap="round"
            style={{transition: 'stroke-dasharray 1s ease-in-out'}}
          />
          <defs>
            <linearGradient id="blueGradient" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="#0f4c75" />
              <stop offset="50%" stopColor="#3282b8" />
              <stop offset="100%" stopColor="#0369a1" />
            </linearGradient>
          </defs>
        </svg>
        <div style={styles.progressInner}>
          <div style={styles.probabilityValue}>
            {percentage.toFixed(1)}%
          </div>
        </div>
      </div>
    );
  };

  return (
    <div style={styles.container}>
      {/* Navigation */}
      <nav style={styles.navbar}>
        <div style={styles.navContent}>
          <div style={styles.logo}>
            <span>ğŸ¯</span>
            ChurnPredictor AI
          </div>
          <div style={styles.badge}>
            OutriX Internship
          </div>
        </div>
      </nav>

      {/* Hero Section */}
      <section style={styles.heroSection}>
        <h1 style={styles.heroTitle}>
          Customer Churn Prediction
        </h1>
        <p style={styles.heroSubtitle}>
          Powered by Machine Learning & Advanced Analytics
        </p>
        <p style={styles.heroDescription}>
          Predict customer churn with 85% accuracy using our AI-powered system. 
          Make data-driven decisions to retain valuable customers and boost revenue.
        </p>
      </section>

      {/* Main Content */}
      <div style={styles.mainContent}>
        {/* Input Form */}
        <div style={styles.card}>
          <div style={styles.cardGlow}></div>
          <h2 style={styles.cardTitle}>
            <span>ğŸ“Š</span>
            Customer Analytics Input
          </h2>

          <div style={styles.inputGrid}>
            <div style={styles.inputGroup}>
              <label style={styles.label}>Gender</label>
              <select 
                name="gender" 
                value={customerData.gender}
                onChange={handleInputChange}
                style={styles.input}
              >
                <option value="Male">ğŸ‘¨ Male</option>
                <option value="Female">ğŸ‘© Female</option>
              </select>
            </div>

            <div style={styles.inputGroup}>
              <label style={styles.label}>Age</label>
              <input 
                type="number" 
                name="age"
                value={customerData.age}
                onChange={handleInputChange}
                style={styles.input}
                min="18" max="100"
                placeholder="Enter age"
              />
            </div>

            <div style={styles.inputGroup}>
              <label style={styles.label}>Tenure (Months)</label>
              <input 
                type="number" 
                name="tenure"
                value={customerData.tenure}
                onChange={handleInputChange}
                style={styles.input}
                min="0" max="72"
                placeholder="Months with company"
              />
            </div>

            <div style={styles.inputGroup}>
              <label style={styles.label}>Monthly Charges</label>
              <input 
                type="number" 
                name="monthly_charges"
                value={customerData.monthly_charges}
                onChange={handleInputChange}
                style={styles.input}
                min="20" max="120" step="0.01"
                placeholder="$0.00"
              />
            </div>

            <div style={styles.inputGroup}>
              <label style={styles.label}>Internet Service</label>
              <select 
                name="internet_service"
                value={customerData.internet_service}
                onChange={handleInputChange}
                style={styles.input}
              >
                <option value="DSL">ğŸŒ DSL</option>
                <option value="Fiber optic">âš¡ Fiber Optic</option>
                <option value="No">âŒ No Internet</option>
              </select>
            </div>

            <div style={styles.inputGroup}>
              <label style={styles.label}>Contract Type</label>
              <select 
                name="contract"
                value={customerData.contract}
                onChange={handleInputChange}
                style={styles.input}
              >
                <option value="Month-to-month">ğŸ“… Month-to-month</option>
                <option value="One year">ğŸ“‹ One Year</option>
                <option value="Two year">ğŸ“œ Two Year</option>
              </select>
            </div>
          </div>

          <button 
            onClick={predictChurn}
            disabled={loading}
            onMouseEnter={() => setButtonHovered(true)}
            onMouseLeave={() => setButtonHovered(false)}
            style={{
              ...styles.button,
              ...(loading ? styles.buttonDisabled : {}),
              ...(buttonHovered && !loading ? styles.buttonHover : {})
            }}
          >
            {loading ? (
              <>
                <span>ğŸ”„</span> Analyzing Customer Data...
              </>
            ) : (
              <>
                <span>ğŸ¤–</span> Predict Churn Risk
              </>
            )}
          </button>
        </div>

        {/* Results Panel */}
        <div style={styles.card}>
          <div style={styles.cardGlow}></div>
          <h2 style={styles.cardTitle}>
            <span>ğŸ“ˆ</span>
            AI Prediction Results
          </h2>

          {prediction ? (
            <>
              {/* Risk Level */}
              <div style={styles.riskIndicator}>
                <div style={getRiskStyle(prediction.risk_level)}>
                  <span>
                    {prediction.risk_level === 'HIGH' ? 'ğŸš¨' : 
                     prediction.risk_level === 'MEDIUM' ? 'âš ï¸' : 'âœ…'}
                  </span>
                  {prediction.risk_level} Risk
                </div>
              </div>

              {/* Circular Progress */}
              <div style={styles.probabilitySection}>
                <div style={styles.probabilityTitle}>Churn Probability</div>
                <CircularProgress percentage={prediction.churn_probability * 100} />
              </div>

              {/* Recommendations */}
              <div style={styles.recommendationsBox}>
                <h3 style={styles.recommendationsTitle}>
                  <span>ğŸ’¡</span>
                  Strategic Recommendations
                </h3>
                {prediction.recommendations.map((rec, index) => (
                  <div key={index} style={styles.recommendationItem}>
                    <span style={{fontSize: '1.2rem'}}>
                      {index === 0 ? 'ğŸ¯' : index === 1 ? 'ğŸ’°' : index === 2 ? 'ğŸ“' : 'ğŸš€'}
                    </span>
                    <span>{rec}</span>
                  </div>
                ))}
              </div>
            </>
          ) : (
            <div style={styles.resultsEmpty}>
              <span style={styles.resultsEmptyIcon}>ğŸ¤–</span>
              <h3>Ready for Analysis</h3>
              <p>Enter customer information on the left and click "Predict Churn Risk" to see AI-powered insights and recommendations.</p>
            </div>
          )}
        </div>
      </div>

