import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_BASE = 'http://localhost:8000';

// Premium Dark Green & Blue Color Palette
const styles = {
  container: {
    minHeight: '100vh',
    background: `
      radial-gradient(circle at 20% 50%, rgba(34, 197, 94, 0.2) 0%, transparent 50%),
      radial-gradient(circle at 80% 20%, rgba(59, 130, 246, 0.2) 0%, transparent 50%),
      radial-gradient(circle at 40% 80%, rgba(6, 78, 59, 0.3) 0%, transparent 50%),
      linear-gradient(135deg, #064e3b 0%, #1e3a8a 50%, #0f172a 100%)
    `,
    fontFamily: "'SF Pro Display', -apple-system, BlinkMacSystemFont, sans-serif",
    position: 'relative',
    overflow: 'hidden'
  },

  backgroundOrbs: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    pointerEvents: 'none',
    zIndex: 0
  },

  orb1: {
    position: 'absolute',
    top: '10%',
    left: '10%',
    width: '300px',
    height: '300px',
    background: 'radial-gradient(circle, rgba(34, 197, 94, 0.1) 0%, transparent 70%)',
    borderRadius: '50%',
    animation: 'float 6s ease-in-out infinite'
  },

  orb2: {
    position: 'absolute',
    top: '60%',
    right: '10%',
    width: '200px',
    height: '200px',
    background: 'radial-gradient(circle, rgba(59, 130, 246, 0.15) 0%, transparent 70%)',
    borderRadius: '50%',
    animation: 'float 8s ease-in-out infinite reverse'
  },

  navbar: {
    background: 'rgba(6, 78, 59, 0.3)',
    backdropFilter: 'blur(30px)',
    padding: '15px 0',
    borderBottom: '1px solid rgba(34, 197, 94, 0.2)',
    position: 'relative',
    zIndex: 10
  },

  navContent: {
    maxWidth: '1400px',
    margin: '0 auto',
    padding: '0 30px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between'
  },

  logo: {
    display: 'flex',
    alignItems: 'center',
    gap: '15px',
    color: '#f0fdf4',
    fontSize: '1.4rem',
    fontWeight: '700'
  },

  logoIcon: {
    width: '40px',
    height: '40px',
    background: 'linear-gradient(135deg, #22c55e, #3b82f6)',
    borderRadius: '12px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: '20px',
    boxShadow: '0 4px 12px rgba(34, 197, 94, 0.3)'
  },

  navBadge: {
    background: 'linear-gradient(135deg, rgba(34, 197, 94, 0.3), rgba(59, 130, 246, 0.2))',
    color: '#f0fdf4',
    padding: '8px 20px',
    borderRadius: '25px',
    fontSize: '0.85rem',
    fontWeight: '600',
    border: '1px solid rgba(34, 197, 94, 0.3)'
  },

  heroSection: {
    textAlign: 'center',
    padding: '80px 30px',
    position: 'relative',
    zIndex: 5
  },

  heroTitle: {
    fontSize: '4.5rem',
    fontWeight: '900',
    marginBottom: '25px',
    background: `
      linear-gradient(135deg, 
        #f0fdf4 0%, 
        #dcfce7 25%, 
        #bbf7d0 50%, 
        #dbeafe 75%, 
        #f0fdf4 100%
      )`,
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
    lineHeight: '1.1',
    textShadow: '0 8px 32px rgba(34, 197, 94, 0.3)',
    animation: 'titleGlow 3s ease-in-out infinite alternate'
  },

  heroSubtitle: {
    fontSize: '1.4rem',
    marginBottom: '15px',
    color: '#dcfce7',
    fontWeight: '500'
  },

  heroDescription: {
    fontSize: '1.1rem',
    color: 'rgba(240, 253, 244, 0.8)',
    maxWidth: '700px',
    margin: '0 auto 40px',
    lineHeight: '1.6'
  },

  statsBar: {
    display: 'flex',
    justifyContent: 'center',
    gap: '60px',
    marginTop: '40px'
  },

  statItem: {
    textAlign: 'center',
    color: '#f0fdf4'
  },

  statNumber: {
    fontSize: '2.5rem',
    fontWeight: '800',
    display: 'block',
    background: 'linear-gradient(135deg, #22c55e, #3b82f6)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent'
  },

  statLabel: {
    fontSize: '0.9rem',
    opacity: 0.8,
    textTransform: 'uppercase',
    letterSpacing: '1px',
    fontWeight: '600',
    color: '#dcfce7'
  },

  mainContent: {
    maxWidth: '1600px',
    margin: '0 auto',
    padding: '0 30px 60px',
    display: 'grid',
    gridTemplateColumns: '1.2fr 1fr',
    gap: '50px',
    marginTop: '-60px',
    position: 'relative',
    zIndex: 5
  },

  card: {
    background: `
      linear-gradient(135deg, 
        rgba(6, 78, 59, 0.4) 0%, 
        rgba(30, 58, 138, 0.3) 50%,
        rgba(15, 23, 42, 0.4) 100%
      )`,
    backdropFilter: 'blur(40px)',
    borderRadius: '32px',
    padding: '50px',
    border: '1px solid rgba(34, 197, 94, 0.2)',
    boxShadow: `
      0 32px 64px rgba(6, 78, 59, 0.3),
      inset 0 1px 0 rgba(34, 197, 94, 0.2)
    `,
    position: 'relative',
    overflow: 'hidden',
    transition: 'all 0.3s ease'
  },

  cardHover: {
    transform: 'translateY(-5px)',
    boxShadow: `
      0 40px 80px rgba(6, 78, 59, 0.4),
      inset 0 1px 0 rgba(34, 197, 94, 0.3)
    `,
    border: '1px solid rgba(34, 197, 94, 0.4)'
  },

  cardGradient: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    height: '3px',
