// Enhanced CSS animations with green/blue theme
const globalStyles = `
  @keyframes float {
    0%, 100% { transform: translateY(0px) rotate(0deg); }
    33% { transform: translateY(-10px) rotate(1deg); }
    66% { transform: translateY(5px) rotate(-1deg); }
  }

  @keyframes titleGlow {
    0%, 100% { text-shadow: 0 8px 32px rgba(34, 197, 94, 0.4); }
    50% { text-shadow: 0 8px 32px rgba(59, 130, 246, 0.4); }
  }

  @keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.8; }
  }

  .animate-pulse { animation: pulse 2s ease-in-out infinite; }

  /* Custom scrollbar */
  ::-webkit-scrollbar {
    width: 8px;
  }

  ::-webkit-scrollbar-track {
    background: rgba(6, 78, 59, 0.3);
  }

  ::-webkit-scrollbar-thumb {
    background: linear-gradient(135deg, #22c55e, #3b82f6);
    border-radius: 4px;
  }

  /* Custom selection */
  ::selection {
    background: rgba(34, 197, 94, 0.3);
    color: #f0fdf4;
  }
`;

function App() {
  const [customerData, setCustomerData] = useState({
    gender: 'Male',
    age: 35,
    tenure: 12,
    monthly_charges: 65.0,
    total_charges: 1500.0,
    internet_service: 'Fiber optic',
    contract: 'Month-to-month',
    payment_method: 'Electronic check',
    paperless_billing: 'Yes',
    tech_support: 'No',
    online_backup: 'No'
  });

  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);
  const [cardHovered, setCardHovered] = useState(false);
  const [buttonHovered, setButtonHovered] = useState(false);

  // Add global styles
  useEffect(() => {
    const styleSheet = document.createElement("style");
    styleSheet.innerText = globalStyles;
    document.head.appendChild(styleSheet);
    return () => document.head.removeChild(styleSheet);
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCustomerData(prev => ({
      ...prev,
      [name]: name === 'age' || name === 'tenure' ? parseInt(value) || 0 : 
             name === 'monthly_charges' || name === 'total_charges' ? parseFloat(value) || 0 : value
    }));
  };

  const predictChurn = async () => {
    setLoading(true);
    try {
      const response = await axios.post(`${API_BASE}/predict`, customerData);
      setPrediction(response.data);
    } catch (error) {
      console.error('Prediction error:', error);
      alert('Error making prediction. Make sure the API is running on port 8000.');
    }
    setLoading(false);
  };

  const getRiskStyle = (riskLevel) => {
    switch(riskLevel) {
      case 'HIGH': return { ...styles.riskBadge, ...styles.riskHigh };
      case 'MEDIUM': return { ...styles.riskBadge, ...styles.riskMedium };
      case 'LOW': return { ...styles.riskBadge, ...styles.riskLow };
      default: return { ...styles.riskBadge, ...styles.riskLow };
    }
  };

  const CircularProgress = ({ percentage }) => {
    const circumference = 2 * Math.PI * 90;
    const strokeDasharray = `${(percentage / 100) * circumference} ${circumference}`;

    return (
      <div style={styles.progressContainer}>
        <svg width="220" height="220" style={{transform: 'rotate(-90deg)'}}>
          <circle
            cx="110"
            cy="110"
            r="90"
            stroke="rgba(34, 197, 94, 0.2)"
            strokeWidth="8"
            fill="transparent"
          />
          <circle
            cx="110"
            cy="110"
            r="90"
            stroke="url(#greenBlueGradient)"
            strokeWidth="8"
            fill="transparent"
            strokeDasharray={strokeDasharray}
            strokeLinecap="round"
            style={{
              transition: 'stroke-dasharray 2s ease-in-out',
              filter: 'drop-shadow(0 0 15px rgba(34, 197, 94, 0.6))'
            }}
          />
          <defs>
            <linearGradient id="greenBlueGradient" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="#22c55e" />
              <stop offset="50%" stopColor="#3b82f6" />
              <stop offset="100%" stopColor="#059669" />
            </linearGradient>
          </defs>
        </svg>
        <div style={styles.progressValue}>
          {percentage.toFixed(1)}%
        </div>
      </div>
    );
  };

  return (
    <div style={styles.container}>
      {/* Background Elements */}
      <div style={styles.backgroundOrbs}>
        <div style={styles.orb1}></div>
        <div style={styles.orb2}></div>
      </div>

      {/* Navigation */}
      <nav style={styles.navbar}>
        <div style={styles.navContent}>
          <div style={styles.logo}>
            <div style={styles.logoIcon}>🎯</div>
            <span>ChurnPredictor AI</span>
          </div>
          <div style={styles.navBadge}>
            OutriX ML Internship
          </div>
        </div>
      </nav>

      {/* Hero Section */}
      <section style={styles.heroSection}>
        <h1 style={styles.heroTitle}>
          Customer Churn
          <br />
          Prediction Engine
        </h1>
        <p style={styles.heroSubtitle}>
          Enterprise-Grade AI Analytics Platform
        </p>
        <p style={styles.heroDescription}>
          Leverage cutting-edge machine learning algorithms to predict customer behavior 
          with unprecedented accuracy. Transform your retention strategy with data-driven insights.
        </p>

        <div style={styles.statsBar}>
          <div style={styles.statItem}>
            <span style={styles.statNumber}>94%</span>
            <span style={styles.statLabel}>Accuracy</span>
          </div>
          <div style={styles.statItem}>
            <span style={styles.statNumber}>5K+</span>
            <span style={styles.statLabel}>Predictions</span>
          </div>
          <div style={styles.statItem}>
            <span style={styles.statNumber}>Real-time</span>
            <span style={styles.statLabel}>Analysis</span>
          </div>
        </div>
      </section>

      {/* Main Content */}
      <div style={styles.mainContent}>
        {/* Input Form */}
        <div 
          style={{
            ...styles.card,
            ...(cardHovered ? styles.cardHover : {})
          }}
          onMouseEnter={() => setCardHovered(true)}
          onMouseLeave={() => setCardHovered(false)}
        >
          <div style={styles.cardGradient}></div>
          <h2 style={styles.cardTitle}>
            <div style={styles.titleIcon}>📊</div>
            Customer Analytics Dashboard
          </h2>

          <div style={styles.formSection}>
            <div style={styles.inputGrid}>
              <div style={styles.inputGroup}>
                <label style={styles.label}>Gender</label>
                <select 
                  name="gender" 
                  value={customerData.gender}
                  onChange={handleInputChange}
                  style={styles.input}
                >
                  <option value="Male">👨 Male</option>
                  <option value="Female">👩 Female</option>
                </select>
              </div>

              <div style={styles.inputGroup}>
                <label style={styles.label}>Age</label>
                <input 
                  type="number" 
                  name="age"
                  value={customerData.age}
                  onChange={handleInputChange}
                  style={styles.input}
                  min="18" max="100"
                  placeholder="Enter customer age"
                />
              </div>

              <div style={styles.inputGroup}>
                <label style={styles.label}>Tenure (Months)</label>
                <input 
                  type="number" 
                  name="tenure"
                  value={customerData.tenure}
                  onChange={handleInputChange}
                  style={styles.input}
                  min="0" max="72"
                  placeholder="Service duration"
                />
              </div>

              <div style={styles.inputGroup}>
                <label style={styles.label}>Monthly Charges</label>
                <input 
                  type="number" 
                  name="monthly_charges"
                  value={customerData.monthly_charges}
                  onChange={handleInputChange}
                  style={styles.input}
                  min="20" max="120" step="0.01"
                  placeholder="$0.00"
                />
              </div>

              <div style={styles.inputGroup}>
                <label style={styles.label}>Internet Service</label>
                <select 
                  name="internet_service"
                  value={customerData.internet_service}
                  onChange={handleInputChange}
                  style={styles.input}
                >
                  <option value="DSL">🌐 DSL Connection</option>
                  <option value="Fiber optic">⚡ Fiber Optic</option>
                  <option value="No">❌ No Internet</option>
                </select>
              </div>

              <div style={styles.inputGroup}>
                <label style={styles.label}>Contract Type</label>
                <select 
                  name="contract"
                  value={customerData.contract}
                  onChange={handleInputChange}
                  style={styles.input}
                >
                  <option value="Month-to-month">📅 Monthly Plan</option>
                  <option value="One year">📋 Annual Contract</option>
                  <option value="Two year">📜 Biennial Plan</option>
                </select>
              </div>
            </div>

            <button 
              onClick={predictChurn}
              disabled={loading}
              onMouseEnter={() => setButtonHovered(true)}
              onMouseLeave={() => setButtonHovered(false)}
              style={{
                ...styles.button,
                ...(loading ? styles.buttonDisabled : {}),
                ...(buttonHovered && !loading ? styles.buttonHover : {})
              }}
            >
              {loading ? (
                <div className="animate-pulse">
                  🔄 Analyzing Customer Profile...
                </div>
              ) : (
                '🚀 Generate AI Prediction'
              )}
            </button>
          </div>
        </div>

        {/* Results Panel */}
        <div style={styles.card}>
          <div style={styles.cardGradient}></div>
          <h2 style={styles.cardTitle}>
            <div style={styles.titleIcon}>🧠</div>
            AI Intelligence Report
          </h2>

          <div style={styles.resultsSection}>
            {prediction ? (
              <>
                {/* Risk Level */}
                <div style={styles.riskIndicator}>
                  <div style={getRiskStyle(prediction.risk_level)}>
                    <span style={{fontSize: '1.5rem'}}>
                      {prediction.risk_level === 'HIGH' ? '🚨' : 
                       prediction.risk_level === 'MEDIUM' ? '⚠️' : '✅'}
                    </span>
                    {prediction.risk_level} Risk Level
                  </div>
                </div>

                {/* Circular Progress */}
                <div style={styles.probabilitySection}>
                  <div style={styles.probabilityTitle}>Churn Probability Score</div>
                  <CircularProgress percentage={prediction.churn_probability * 100} />
                </div>

                {/* Recommendations */}
                <div style={styles.recommendationsBox}>
                  <h3 style={styles.recommendationsTitle}>
                    <span>💡</span>
                    Strategic Action Plan
                  </h3>
                  {prediction.recommendations.map((rec, index) => (
                    <div key={index} style={styles.recommendationItem}>
                      <div style={styles.recommendationIcon}>
                        {index === 0 ? '🎯' : index === 1 ? '💰' : index === 2 ? '📞' : '🚀'}
                      </div>
                      <span>{rec}</span>
                    </div>
                  ))}
                </div>
              </>
            ) : (
              <div style={styles.emptyState}>
                <span style={styles.emptyIcon}>🤖</span>
                <h3 style={styles.emptyTitle}>AI Engine Ready</h3>
                <p>
                  Input customer data to generate intelligent predictions and 
                  receive personalized retention strategies powered by machine learning.
                </p>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer style={styles.footer}>
        <div style={styles.footerContent}>
          <p style={{fontSize: '1.1rem', marginBottom: '10px'}}>
            🎓 Built for OutriX AI/ML Internship - Task 3
          </p>
          <p style={{opacity: 0.8}}>
            ⚡ Enterprise React.js + FastAPI + XGBoost Architecture
          </p>
        </div>
      </footer>
    </div>
  );
}

export default App;
